/// <reference types="mongoose" />
import * as mongoose from 'mongoose';
export interface IOrder {
    consigneeName: String;
    consigneeAddress: String;
    consigneePhone: String;
    senderName: String;
    senderAddress: String;
    senderPhone: String;
    createTime: Date;
    tax: Number;
    shipping: Number;
    paid: Number;
    paidTime: Date;
    status: String;
    orderItems: IOrderItem[];
}
export interface IOrderItem {
    product: IProduct;
    productQuantity: Number;
    productCost: Number;
    productOrderPrice: Number;
}
export interface IProduct {
    productName: String;
    productSN?: String;
    productSummary: String;
    productDetail: String;
    productPrice: Number;
    productUnit: String;
}
export interface IOrderModel extends IOrder, mongoose.Document {
    _id: String;
}
export interface IOrderItemModel extends IOrderItem, mongoose.Document {
    _id: String;
}
export interface IProductModel extends IProduct, mongoose.Document {
    _id: String;
}
export declare const OrderDAO: mongoose.Model<IOrderModel>;
export declare const ProductDAO: mongoose.Model<IOrderModel>;
